@app.get("/features/")
def get_feature():
    return FEATURES_MOCK_DATA.values()


@app.get("/features/{feature_id}")
def get_feature(feature_id: int):
    return FEATURES_MOCK_DATA[feature_id]


@app.post("/adjust_feature/")
def adjust_feature(image_id: int, feature_id: int, activation: int):
    for image_features in FEATURE_IMAGE_MOCK_DATA.values():
        if (
            image_features["image_id"] == image_id
            and image_features["feature_id"] == feature_id
        ):
            image_features["activation"] = activation
            break
    return IMAGE_MOCK_DATA[image_id]


@app.get("images/")
def get_all_images():
    return IMAGE_MOCK_DATA.values()


@app.get("/images/{image_id}")
def get_image(image_id: int):
    return IMAGE_MOCK_DATA[image_id].get_info()
